import { Button, Input, makeStyles } from '@mui/material';
import React, { useState} from 'react';
import { Link } from 'react-router-dom';

const Connect = () => {
    const [username, setUsername] = useState('');
    const [room, setRoom] = useState('');

    return (
        <div className="connectOuterContainer">
            <div className="connectInnerContainer">
                <h1 className="heading">Connect to a Room</h1>
                <div><Input placeholder="" className="connectInput" type="text" value={username} onChange={(event) => setUsername(event.target.value)} /></div>
                <div><Input placeholder="" className="chatInput" sx={{ mt: 2 }} type="text" value={room} onChange={(event) => setRoom(event.target.value)} /></div>
                <Link onClick={event => (!username || !room) ? event.preventDefault() : null} to={{ pathname: "/chat", state: { username, room } }}>
                    <Button className="submitButton" sx={{ mt: 2 }} color="success" variant="contained">Join Room</Button>
                </Link>
            </div>
        </div>
    )
}

export default Connect;
