import { Box, Button, Container, Input, Typography } from '@mui/material';
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Connect = () => {
    const [username, setUsername] = useState('');
    const [room, setRoom] = useState('');

    return (
        <Box className="connectOuterContainer">
            <Box className="connectInnerContainer">
                <Container className="connectHeader" sx={{ color: 'white'}}>
                    text
                </Container>
                <Typography variant="h4" className="heading">Connect to a Room</Typography>
                <Box className="connectInput">
                    <Typography variant="h6" sx={{ mt: 5, color: 'white' }}>Username</Typography>
                    <Input placeholder="insert username"
                    className="connectInput"
                    sx={{ color: 'white'}}
                    type="text"
                    value={username}
                    onChange={(event) => setUsername(event.target.value)} />
                </Box>
                <Box className="connectInput">
                    <Typography variant="h6" sx={{ mt: 5, color: 'white'  }}>Room Code</Typography>
                    <Input placeholder="insert room code"
                    className="chatInput"
                    type="text"
                    value={room}
                    onChange={(event) => setRoom(event.target.value)} />
                </Box>
                <Link onClick={event => (!username || !room) ? event.preventDefault() : null} to={{ pathname: "/chat", state: { username, room } }}>
                    <Button className="submitButton" sx={{ mt: 5, mb: 5}} color="success" variant="contained">Join Room</Button>
                </Link>
            </Box>
        </Box>
    )
}

export default Connect;
