import { Box, Button, Container, Input, Typography } from '@mui/material';
import { styled } from '@mui/system';
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

//Defining all styled components for this component
const StyledTypography = styled(Typography)({
    color: 'white',
});

const StyledInput = styled(Input)({
    color: 'white',
});

const StyledContainer = styled(Container)({
    color: 'white',
    fontSize: 10,
})

const Connect = () => {
    const [username, setUsername] = useState('');
    const [room, setRoom] = useState('');

    return (
        <Box className="connectOuterContainer">
            <Box className="connectInnerContainer">
                <StyledContainer className="connectHeader">
                    text
                </StyledContainer>
                <StyledTypography variant="h4" className="heading">Connect to a Room</StyledTypography>
                <Box className="connectInput">
                    <StyledTypography variant="h6" sx={{ mt: 5 }}>Username</StyledTypography>
                    <StyledInput placeholder="insert username" className="connectInput" type="text" value={username} onChange={(event) => setUsername(event.target.value)} />
                </Box>
                <Box className="connectInput">
                    <StyledTypography variant="h6" sx={{ mt: 5 }}>Room Code</StyledTypography>
                    <StyledInput placeholder="insert room code" className="chatInput" type="text" value={room} onChange={(event) => setRoom(event.target.value)} />
                </Box>
                <Link onClick={event => (!username || !room) ? event.preventDefault() : null} to={{ pathname: "/chat", state: { username, room } }}>
                    <Button className="submitButton" sx={{ mt: 5, mb: 5 }} color="success" variant="contained">Join Room</Button>
                </Link>
            </Box>
        </Box>
    )
}

export default Connect;
