import { Box, Button, Input, Typography } from '@mui/material';
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Connect = () => {
    const [username, setUsername] = useState('');
    const [room, setRoom] = useState('');

    return (
        <Box className="connectOuterContainer">
            <Box className="connectInnerContainer">
                <Typography variant="h(" className="heading">Connect to a Room</Typography>
                <Box className="connectInput">
                    <Typography variant="h3">Username</Typography>
                    <Input placeholder="" className="connectInput" type="text" value={username} onChange={(event) => setUsername(event.target.value)} />
                </Box>
                <Box className="connectInput">
                    <Typography variant="h3">Room Code</Typography>
                    <Input placeholder="" className="chatInput" type="text" value={room} onChange={(event) => setRoom(event.target.value)} />
                </Box>
                <Link onClick={event => (!username || !room) ? event.preventDefault() : null} to={{ pathname: "/chat", state: { username, room } }}>
                    <Button className="submitButton" sx={{ mt: 5, mb: 5 }} color="success" variant="contained">Join Room</Button>
                </Link>
            </Box>
        </Box>
    )
}

export default Connect;
